import React, { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Image, TouchableOpacity, TextInput, KeyboardAvoidingView } from 'react-native';
import { AsyncStorage } from 'react-native';


const EditProfileScreen = ({ username, onSave, onCancel }) => {
  const [newUsername, setNewUsername] = useState(username);

  const handleSave = () => {
    onSave(newUsername);
  }
  
  return (
    <>
      {/* <Text>Enter new username:</Text> */}
      <TextInput
        style={styles.input}
        onChangeText={(text) => setNewUsername(text)}
        value={newUsername}
      />
      <TouchableOpacity onPress={handleSave}>
        <Text style={styles.saveProfile}>Save</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={onCancel}>
        <Text style={styles.cancelProfile}>Cancel</Text>
      </TouchableOpacity>
    </>
  );
};

const ProfileUsername = () => {
  const [username, setUsername] = useState("Username");
  const [showForm, setShowForm] = useState(false);

  const handleEditProfile = () => {
    setShowForm(true);
  }
  
  const handleSaveProfile = (newUsername) => {
    setUsername(newUsername);
    setShowForm(false);
  }

  const handleCancelProfile = () => {
    setShowForm(false);
  }

  return (
    <SafeAreaView style={styles.container}>
      <SafeAreaView style={styles.box1}>
        
      <Image
          source={require('../../assets/health.png')}
          style={styles.image}
        />
        
        
        <Text style={styles.profileDetail}>{username}</Text>
        <TouchableOpacity onPress={handleEditProfile}>
          <Text style={styles.editProfile}>Edit profile</Text>
        </TouchableOpacity>
      </SafeAreaView>
      {showForm && (
        <KeyboardAvoidingView
          behavior="padding"
          style={styles.box2}
        >
          <EditProfileScreen
            username = {username}
            onSave={handleSaveProfile}
            onCancel={handleCancelProfile}
          />
        </KeyboardAvoidingView>
      )}
      {!showForm && (
        <SafeAreaView style={styles.box2}></SafeAreaView>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column',
  },
  box1: {
    flex: 1,
    backgroundColor: 'white',
    height: 150,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  box2: {
    flex: 1,
    backgroundColor: '#00000000',
    height: 80,
    width: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  
  image: {
    width: 90,
    height: 90,
    borderRadius: 50,
    backgroundColor:'black',
  },

  profileDetail: {
    fontSize: 30,
    color: 'black',
    alignItems: 'center',
    justifyContent: 'center',
    fontWeight: 'bold',
  },

  editProfile: {
    fontSize: 18,
    color: 'blue',
  },

  saveProfile: {
    fontSize: 22,
    color: 'blue',
    textDecorationLine: 'underline',
  },

  input: {
    height: 40,
    width: '80%',
    borderColor: 'black',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 50,
    shadowRadius: 2,
    elevation: 4,
    textAlign: 'center',
  }

});

export default ProfileUsername;
