import React from 'react';
import { StyleSheet, Text, View, Image, Dimensions, SafeAreaView } from 'react-native';

const screenWidth = Dimensions.get('window').width;

const AppleNutritionFacts = () => {
  const nutritionFacts = [
    { label: 'Calories', value: '52 kcal' },
    { label: 'Water', value: '86%' },
    { label: 'Protein', value: '0.3g' },
    { label: 'Carbs', value: '13.8g' },
    { label: 'Sugar', value: '10.4g' },
    { label: 'Fiber', value: '2.4g' },
    { label: 'Fat', value: '0.2g' },
    { label: 'Vitamin C', value: '4.6mg' },
  ];

  return (
    <View style={styles.appleNutritionFacts}>
      <Image
        style={styles.placeholderImage}
        source={{ uri: 'https://upload.wikimedia.org/wikipedia/commons/1/15/Red_Apple.jpg' }}
      />
      <Text style={styles.h1}>Apple Nutrition Facts</Text>
      {nutritionFacts.map((fact, index) => (
        <View style={styles.row} key={index}>
          <Text style={styles.label}>{fact.label}</Text>
          <Text style={styles.value}>{fact.value}</Text>
        </View>
      ))}
    </View>
  );
};

const App = () => {
  return (
    <SafeAreaView style={styles.container}>
      <AppleNutritionFacts />
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  appleNutritionFacts: {
    width: screenWidth * 0.9,
    padding: 20,
    backgroundColor: '#FFFFFF',
    borderRadius: 15,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.25,
    shadowRadius: 3,
    elevation: 5,
  },
  h1: {
    fontSize: 24,
    fontWeight: '600',
    marginBottom: 20,
    textAlign: 'center',
  },
  placeholderImage: {
    width: 150,
    height: 150,
    alignSelf: 'center',
    borderRadius: 15,
    marginBottom: 20,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 8,
  },
  label: {
    fontSize: 16,
    fontWeight: '400',
  },
  value: {
    fontSize: 16,
    fontWeight: '400',
  },
});

export default App;
