import React, { useState, useEffect } from 'react';
import { Button, TextInput, View, StyleSheet, TouchableOpacity, Text, Alert, FlatList } from 'react-native';
import { BarCodeScanner } from 'expo-barcode-scanner';
import { AntDesign } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

function AddFood({ route, name }) {
    const [permissionResponse, requestPermission] = BarCodeScanner.usePermissions();
    const [hasPermission, setHasPermission] = useState(null)
    const [query, setQuery] = useState('');
    const [result, setResult] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [barcode, setBarcode] = useState(null);
    const [currentFood, setCurrentFood] = useState(null);
    const [showScanner, setShowScanner] = useState(false);

    const handleInputChange = (text) => {
        setSearchTerm(text);
    };

    const BreakFast = [
        { key: 'item1', value: 'Eggs' },
        { key: 'item2', value: 'Orange Juice' },
    ];

    const searchFoods = () => {
        const apiKey = 'sd8DWPmBSnFocMzJvTzQ6JcE1vHRxsabdd08JOC8';
        const endpoint = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=${apiKey}&query=${query}`;

        fetch(endpoint).then(response => response.json()).then(data => {
            const results = data.foods.map(item => ({ key: item.fdcId, name: item.description }));
            setResult(results);
        }).catch(error => console.log(error));

        console.log(result + '\n' + '\n');
    }

    const renderItem = ({ item }) => {
        return (
            <View key={item.key}>
                <TouchableOpacity style={{ padding: 10, borderBottomColor: 'black', borderBottomWidth: 1 }}>
                    <Text>{item.name}</Text>
                </TouchableOpacity>
            </View>
        );
    };



    const handleSearchClick = () => {
        const apiKey = 'sd8DWPmBSnFocMzJvTzQ6JcE1vHRxsabdd08JOC8';
        const dataType = ["Survey (FNDDS)"];
        const pageSize = 20;
        const endpoint = `https://api.nal.usda.gov/fdc/v1/foods/search?api_key=${encodeURIComponent(apiKey)}&query=${encodeURIComponent(searchTerm)}&dataType=${encodeURIComponent(dataType)}&pageSize=${encodeURIComponent(pageSize)}`;

        fetch(endpoint).then(response => response.json()).then(data => {
            const results = data.foods.map(item => ({ key: item.fdcId, name: item.description }));
            setResult(results);
        }).catch(error => console.log(error));

        // console.log(result[40].name + '\n' + '\n');
    };

    const navigation = useNavigation();


    const requestCameraPermission = async () => {
        const { status } = await requestPermission();
        setHasPermission(status === 'granted');
    };

    const handleBarCodeVisibility = () => {
        if (showScanner == false) {
            setShowScanner(true);
        }

        else {
            setShowScanner(false);
        }
    }

    const handleScanBarcodePress = () => {
        // send alert to user to manually turn on camera
        requestCameraPermission()
        if (permissionResponse.canAskAgain == false) {
            Alert.alert('Camera disabled', 'Enable manually on settings > Expo Go > Camera')
        }

        else if (permissionResponse.granted) {
            handleBarCodeVisibility()
        }

    };

    const handleBarCodeScanned = ({ type, data }) => {
        alert(`Bar code with type ${type} and data ${data} has been scanned!`);
        setBarcode(data);
        setShowScanner(false);

    };

    const handleFloatingButton = () => {
        //navigation.navigate('BarcodeSearch');
        navigation.navigate('Macro');
    };

    return (

        <View style={{ flex: 1 }}>
            <Text>{name}</Text>
            <View style={styles.container}>
                <TextInput
                    style={styles.input}
                    value={searchTerm}
                    onChangeText={handleInputChange}
                    placeholder="Search..."

                />

                <Button title="Search" onPress={handleSearchClick} />
                <TouchableOpacity onPress={handleScanBarcodePress}>
                    <AntDesign name="scan1" size={24} color="black" />
                </TouchableOpacity>

            </View>
            {/* <View style={styles.floating}>
                <TouchableOpacity onPress={handleFloatingButton}>
                    <AntDesign name="plus" size={24} color="white" />
                </TouchableOpacity>
                
            </View> */}
            {showScanner ? (
                <BarCodeScanner
                    onBarCodeScanned={handleBarCodeScanned}
                    style={styles.camera}
                />
            ) : (null)
            }

            <FlatList
                data={result}
                renderItem={renderItem}
                keyExtractor={(item) => item.key}
            />
            
            {/*
            Help!!
            Direct user to  `ManualLogToDB` component `Log Manually` press */
            }
            <View style={styles.addManualContainer}>
                <TouchableOpacity style={styles.AddFoodManually} >
                    <Text style={styles.buttonText}>Log {'\n'}Manually</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}


export default AddFood;

const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
    },
    input: {
        flex: 1,
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 10,
    },
    floating: {
        position: 'absolute',
        bottom: 20,
        right: 20,
        backgroundColor: 'blue',
        borderRadius: 25,
        width: 50,
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
    },

    camera: {
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'center',
    },

    // add food manually - start
    addManualContainer:{
        justifyContent:'flex-end',
        alignItems:'flex-end',
    },
    AddFoodManually: {
        marginTop:5,
        marginRight:15,
        marginBottom:15,
        justifyContent: 'center',
        alignContent: 'center',
        alignSelf:'auto',
        borderColor:'black',
        borderWidth:1,
        height:88,
        width:88,
        borderRadius:50,
    },
    buttonText: {
        fontSize: 18,
        fontWeight: 'bold',
        textAlign: 'center',
        justifyContent:'center',
        alignItems:'center',
        
    }
    // add food manually - end



});

