import React, { useState } from 'react';
import { Text, TextInput, View } from 'react-native';

const CalorieGoal = () => {
  const [goal, setGoal] = useState('');

  return (
    <View>
      <Text>Please enter your calorie goal:</Text>
      <TextInput
        onChangeText={(text) => setGoal(text)}
        value={goal}
        placeholder="Calorie Goal"
        keyboardType="numeric"
        style={{
          borderWidth: 1,
          borderColor: 'gray',
          padding: 10,
          margin: 10,
        }}
      />
      {goal !== '' ? (
        <Text>Your calorie goal is: {goal}</Text>
      ) : (
        <Text>Please enter a goal</Text>
      )}
    </View>
  );
};

export default CalorieGoal;
