import { SafeAreaView, Text, StyleSheet, ScrollView, TextInput, TouchableOpacity, Keyboard, TouchableWithoutFeedback } from 'react-native';
import React, { useState } from 'react';

const ProfilePage = () => {
    const rows = [
        { description: 'UserName', content: 'Full name' },
        { description: 'Height', content: '7ft.' },
        { description: 'Sex', content: 'F' },
        { description: 'DOB', content: 'n/a' },
        { description: 'Location', content: 'n/a' },
        { description: 'Email', content: 'name@host.xyz' },
        { description: 'Goals', content: 'n/a' },
    ];

    return (
        <ScrollView>
            <SafeAreaView style={{ flex: 1, justifyContent: 'center' }}>
                <SafeAreaView style={styles.row}>
                    <SafeAreaView style={styles.rowNContaier}>
                        <Text style={styles.description}>Profile</Text>
                        <SafeAreaView style={styles.content}>
                            <Text style={styles.image}></Text>
                        </SafeAreaView>
                    </SafeAreaView>
                </SafeAreaView>

                {rows.map(({ description }, index) => (
                    <SafeAreaView style={styles.row} key={index}>
                        <SafeAreaView style={styles.rowNContaier}>
                            <Text style={styles.description}>{description}</Text>
                            <SafeAreaView style={styles.content}>
                                <Text>{rows[index].content}</Text>
                            </SafeAreaView>
                        </SafeAreaView>
                    </SafeAreaView>
                ))}
            </SafeAreaView>
        </ScrollView>

    );
};

const styles = StyleSheet.create({
    rowNContaier: {
        flexDirection: 'row',
        height: 80,
        marginLeft: 10,
        marginRight: 10,
    },
    row: {
        flexDirection: 'column',
        height: 80,
        marginLeft: 10,
        marginRight: 10,
        justifyContent: 'center',
    },
    description: {
        height: 50,
        borderRadius: 10,
        paddingLeft: 15,
        flex: 1,
        paddingTop: 19,
        marginTop: 10,
    },
    content: {
        borderRadius:50,
        height: 80,
        width: '33.33%',
        alignItems: 'center',
        justifyContent: 'center',
    },

    image: {
        height: 80,
        width: 80,
        borderRadius: 38,
        alignItems: 'center',
        justifyContent: 'center',
        borderColor: 'gray',
        
        borderWidth: 1,
    }
})

export default ProfilePage;
